import React, { useState } from 'react';
import axiosInstance from '../../components/connection';
import { useHistory } from 'react-router-dom';
import { Form, Input, Button,Checkbox } from 'antd';
import { NavLink } from 'react-router-dom';




export default function RegistrationForm() {

    const history = useHistory();
	const initialFormData = Object.freeze({
		email: '',
		username: '',
		password: '',
	});

	const [formData, updateFormData] = useState(initialFormData);

	const handleChange = (e) => {
		updateFormData({
			...formData,
			// Trimming any whitespace
			[e.target.name]: e.target.value.trim(),
		});
	};

	const handleSubmit = (e) => {
		e.preventDefault();
		console.log(formData);

		axiosInstance
			.post(`user/create/`, {
				email: formData.email,
				user_name: formData.username,
				password: formData.password,
			})
			.then((res) => {
				history.push('/');
				console.log(res);
				console.log(res.data);
			});
	};

    return (
      <Form name="basic" labelCol={{  span: 8,}} wrapperCol={{span: 16,}}initialValues={{remember: true,}}>


    <Form.Item id="email" label="Email"   name="eamil" rules={[{required: true,message: 'Please input your email!', }, ]} onChange={handleChange}>
        <Input/>
      </Form.Item>


      <Form.Item  id="username" label="Username" name="username"  rules={[{ required: true, message: 'Please input your username!', }, ]} onChange={handleChange}>
        <Input/>
      </Form.Item>



      <Form.Item  id="password" label="Password" name="password" rules={[ {  required: true, message: 'Please input your password!', }, ]} onChange={handleChange}>
        <Input.Password/>
      </Form.Item>



      <Form.Item name="remember" valuePropName="checked" wrapperCol={{ offset: 8,  span: 16,}}>
        <Checkbox>Remember me</Checkbox>
      </Form.Item>

      <Form.Item wrapperCol={{offset: 8, span: 16,
        }}
      >
        <Button  onClick={handleSubmit} type="primary" htmlType="submit">
          Submit
        </Button>
        Or 
                    <NavLink 
                        style={{marginRight: '10px'}} 
                        to='/login/'> Login
                    </NavLink>
      </Form.Item>
    </Form>
    );

}
