import React from 'react';
import { Form, Input, Button, Spin,Checkbox } from 'antd';
import { connect } from 'react-redux';
import { NavLink ,Redirect} from 'react-router-dom';
import { login } from '../store/action/auth';
import Icon from '@ant-design/icons';
import PropTypes from 'prop-types';


const antIcon = <Icon type="loading" style={{ fontSize: 24 }} spin />;


class NormalLoginForm extends React.Component {
  handleSubmit = (e) => {
    this.state = {
      username: '',
      password: '',
    };

     PropTypes= {
      login: PropTypes.func.isRequired,
      isAuthenticated: PropTypes.bool,
    };

    // onSubmit = (e) => {
    //   e.preventDefault();
    //   this.props.login(this.state.username, this.state.password);
    // };

    this.handleChange = (e) => this.setState({ [e.target.name]: e.target.value });
  }






  render(){
    if (this.props.isAuthenticated) {
      return <Redirect to="/" />;
    }
    const { username, password } = this.state.value;
    return(
      <div>
        <Form name="basic" labelCol={{span: 8,}} wrapperCol={{span: 16,}} initialValues={{remember: true, }}>

          <Form.Item label="Username" name="username" rules={[{required: true,message: 'Please input your username!', },]}>
          <Input />
          </Form.Item>

          <Form.Item label="Password" name="password" rules={[{required: true,message: 'Please input your password!',},]}>
          <Input.Password />
          </Form.Item>

        <Form.Item name="remember" valuePropName="checked"  wrapperCol={{offset: 8, span: 16,}}>
        <Checkbox>Remember me</Checkbox>
        </Form.Item>

        <Form.Item wrapperCol={{offset: 8,span: 16,}}>
        <Button type="primary" htmlType="submit">
          Submit
        </Button>
          Or 
        <NavLink style={{marginRight: '10px'}} to='/signup/'> 
          signup
        </NavLink>
        </Form.Item>

      </Form>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  isAuthenticated: state.auth?.isAuthenticated,
});


export default connect(mapStateToProps,{login})(NormalLoginForm);
