import React , { useState, useEffect  } from "react";
import { Form, Input, Button} from "antd";
import HrLayout from "../../../containers/templates/hr_manager/hrlayout";
import axiosInstance from "../../connection";
import { useHistory, useParams} from 'react-router-dom';
import Employee from "../employee/employee";


const FormItem = Form.Item;


export default function AnnouncementCreateForm () {



    const { announcementID } = useParams();
  const history = useHistory();
	const initialFormData = Object.freeze({
		Message: '',
		From: '',
		To : '',
        Business_hr: '',
        Employee: '',
	});



	const [formData, updateFormData] = useState(initialFormData);

    useEffect(() => {
		axiosInstance.get(`hr/Employee/${announcementID}/`).then((res) => {
			updateFormData({
				...formData,
				['Business_hr']: res.data. Business,
				['Employee']: res.data.First_name,
			});
			console.log(res.data);
		});
	}, [updateFormData]);

	const handleChange = (e) => {
		updateFormData({
			...formData,
			// Trimming any whitespace
			[e.target.name]: e.target.value.trim(),
		});
	};

	const handleSubmit = (e) => {
		e.preventDefault();
		console.log(formData);

		axiosInstance
			.post(`hr/Announcement/`, {
                Message: formData.Message,
                From:formData.From,
                To:formData.To,
                Business_hr:formData.Business_hr,
                Employee:formData.Employee,
      // time:formData.time,

			})


			.then((res) => {
				history.push('/announcement');
				console.log(res);
				console.log(res.data);
			});
	};



    return (
      <div>
          <HrLayout>
        <Form>

          <FormItem label="Message">
            <Input 
            id="Message"
            name="Message" 
						label="Message"
						onChange={handleChange}
            placeholder="Input a Message here" 
            />
          </FormItem>

          <FormItem label="From">
            <Input 
            id="From"
            name="From" 
			label="From"
			onChange={handleChange}
            placeholder="Input a business from here" 
            />
          </FormItem>

          <FormItem label="To">
            <Input 
            id="To"
            name="To" 
			label="To"
			onChange={handleChange}
            placeholder="Input where a message is to  here" 
            />
          </FormItem>

          <FormItem label=" Business_hr"
          id=" Business_hr"
          name=" Business_hr" 
          onChange={handleChange}
          >
          </FormItem>

          <FormItem label="Employee"
           id="Employee"
           name="Employee" 
           onChange={handleChange}
          >
          </FormItem>






        {/* <Form.Item label="Date of Birth">
        <DatePicker label="Date_of_birth" placeholder="Enter some content ..." />
        </Form.Item> */}



          <FormItem>
            <Button 
            type="submit"
			color="primary"
			onClick={handleSubmit}
            >
            Create
            </Button>
          </FormItem>

        </Form>
        </HrLayout>
      </div>
    );

}
