import React from "react";
import axios from "axios";
import { connect } from "react-redux";
import { Card } from "antd";
import { JobLayout } from "../../templates/joblayout";
import axiosInstance from "../../../components/connection";




class ApplicationDetail extends React.Component {
  state = {
    applications: {}
  };



  componentDidMount(){
    const applicationID = this.props.match.params.applicationID;
    axiosInstance
    .get(`job/Applications/${applicationID}/`).then(res => {
      this.setState({
        applications: res.data
      });
    });
  }





  render() {
    return (
      <div>
        <JobLayout>
        <Card title={this.state.applications.Applicant}>
          <p> Job_listing:{this.state.applications.Job_listing} </p>
        </Card>
        </JobLayout>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    token: state.token
  };
};

export default ApplicationDetail;
