import React , { useState, useEffect  } from "react";
import { Form, Input, Button} from "antd";
import { HrLayout } from "../../../containers/templates/hr_manager/hrlayout";
import axiosInstance from "../../connection";
import { useHistory, useParams} from 'react-router-dom';




const FormItem = Form.Item;


export default function ComplainCreateForm () {

  const history = useHistory();
	const initialFormData = Object.freeze({
        Staff_issue: '',
        Disciplinary_Action: '',
        Business_hr: '',
        Employee : '',

	});

    let userDetails = JSON.parse(localStorage.getItem('user'));
    console.log(userDetails.email);


	const [formData, updateFormData] = useState(initialFormData);



	const handleChange = (e) => {
		updateFormData({
			...formData,
			// Trimming any whitespace
			[e.target.name]: e.target.value.trim(),
		});
	};

	const handleSubmit = (e) => {
		e.preventDefault();
		console.log(formData);

		axiosInstance
			.post(`hr/Complain/`, {
                Staff_issue:formData.Staff_issue,
                Disciplinary_Action:formData.Disciplinary_Action,
                Business_hr:formData.Business,
                Employee :formData.First_name,
      // time:formData.time,

			})


			.then((res) => {
				history.push('/complain/');
				console.log(res);
				console.log(res.data);
			});
	};



    return (
      <div>
          <HrLayout>
        <Form>
        <FormItem label="Staff_issue">
            <Input 
            id="Staff_issue"
            name="Staff_issue" 
			label="Staff_issue"
			onChange={handleChange}
            placeholder="Input a staff issue here" 
            />
          </FormItem>



          <FormItem label="Disciplinary_Action">
            <Input 
            id="Disciplinary_Action"
            name="Disciplinary_Action" 
			label="Disciplinary_Action"
			onChange={handleChange}
            placeholder="Input a business disciplinary action here" 
            />
          </FormItem>


          <FormItem label="Business">
            <Input 
            id="Business_hr"
            name="Business_hr" 
			label="Business_hr"
			onChange={handleChange}
            placeholder="Input a your business  here" 
            />
          </FormItem>

          <FormItem label="Employee ">
            <Input 
            	id="Employee "
              name="Employee " 
             label="Employee "
             onChange={handleChange}
            placeholder="Input a employee  here" 
            />
          </FormItem>


          <FormItem>
            <Button 
            type="submit"
			color="primary"
			onClick={handleSubmit}
            >
            Create
            </Button>
          </FormItem>

        </Form>
        </HrLayout>
      </div>
    );

}
