import React from "react";
import JobListing from "../../../components/job_application/job_listing/job_listing"
import axiosInstance from "../../../components/connection";
import { JobLayout } from "../../templates/joblayout";



class JobList extends React.Component {
  state = {
    job: []
  };

  fetchJob = () => {
    axiosInstance
    .get("job/Listing/").then(res => {
      this.setState({
        job: res.data
      });
    });
  }

  componentDidMount() {
    this.fetchJob();
  }

  componentWillReceiveProps(newProps) {
    if (newProps.token) {
      this.fetchJob();      
    }
  }

  render() {
    return (
      <div>
        <JobLayout>
        <JobListing data={this.state.job} /> <br />
        </JobLayout>
      </div>
    );
  }
}

export default JobList;
