import React from "react";
import axios from "axios";
import Business from "../../../components/business/business";
import axiosInstance from "../../../components/connection";
import { ApplicantLayout } from "../../templates/applicant/applicantlayout";
import {Button} from "antd";
import { Link, withRouter } from 'react-router-dom';



class BusinessList extends React.Component {
  state = {
    businesses: []
  };

  fetchBusiness = () => {
    axiosInstance
    .get("business/Business/").then(res => {
      this.setState({
        businesses: res.data
      });
    });
  }

  componentDidMount() {
    this.fetchBusiness();
  }

  componentWillReceiveProps(newProps) {
    if (newProps.token) {
      this.fetchBusiness();      
    }
  }

  render() {
    return (
      <div>
        <ApplicantLayout>

        <Button type="primary" htmlType="submit" onClick="location.href='/businesscreate'">
        <Link to="/businesscreate">Create</Link>
            </Button>


        <Business data={this.state.businesses} /> <br />
        </ApplicantLayout>
      </div>
    );
  }
}

export default BusinessList;
