import React from "react";
import axios from "axios";
import { connect } from "react-redux";
import { Button, Card } from "antd";
// import CustomForm from "../components/Form";


class JobDetail extends React.Component {
  state = {
    job: {}
  };


  componentDidMount(){
    event.preventDefault();
    const jobID = this.props.match.params.jobID;
    axios.get(`http://127.0.0.1:8000/api_application/Listing/${jobID}/`).then(res => {
      this.setState({
        job: res.data
      });
    });
  }

  handleDelete = event => {
    event.preventDefault();
    const jobID = this.props.match.params.jobID;
    axios.defaults.headers = {
      "Content-Type": "application/json",
      Authorization: `Token ${this.props.token}`
    };


    axios.delete(`http://127.0.0.1:8000/api_application/Listing/${jobID}/delete/`)
    .then(res => {
      if (res.status === 204) {
        this.props.history.push(`/`);
      }
    })
  };

  render() {
    return (
      <div>
        <Card title={this.state.job.Position}>
          <p> {this.state.job.Position} </p>
          <p> {this.state.job.Education_level} </p>
        </Card>
