import React from "react";
import axios from "axios";
import { connect } from "react-redux";
import { Button, Card } from "antd";



class JobDetail extends React.Component {
  state = {
    job: {}
  };


  componentDidMount(){
    const jobID = this.props.match.params.jobID;
    axios.get(`http://127.0.0.1:8000/job/Listing/${jobID}/`).then(res => {
      this.setState({
        job: res.data
      });
    });
  }

  handleDelete = event => {
    event.preventDefault();
    const jobID = this.props.match.params.jobID;
    axios.defaults.headers = {
      "Content-Type": "application/json",
      Authorization: `Token ${this.props.token}`
    };
    axios.delete(`http://127.0.0.1:8000/job/Listing/${jobID}/delete/`)
    .then(res => {
      if (res.status === 204) {
        this.props.history.push(`/applicants`);
      }
    })
  };



  render() {
    return (
      <div>
        <Card title={this.state.job.Position}>
          <p> Post:{this.state.job.Position} </p>
         <p> Level of education:{this.state.job.Education_level} </p>
        </Card>


      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    token: state.token
  };
};

export default connect(mapStateToProps)(JobDetail);
