import React , { useState, useEffect } from "react";
import { Form, Input, Button,DatePicker} from "antd";
import axiosInstance from "../../connection";
import { useHistory, useParams} from 'react-router-dom';
import { HrLayout } from "../../../containers/templates/hr_manager/hrlayout";
import Employee from "../employee/employee";


const FormItem = Form.Item;


export default function ComplainCreateForm () {


  const history = useHistory();
  const { employeeID } = useParams();
    const { complainID } = useParams();
	const initialFormData = Object.freeze({
		Staff_issue: '',
		Date: '',
        Disciplinary_Action: '',
        Business_hr: '',
        Employee : '',
	});

	const [formData, updateFormData] = useState(initialFormData);

	useEffect(() => {
		axiosInstance.get(`hr/Employee/${employeeID}/`).then((res) => {
			updateFormData({
				...formData,
				['Employee']: res.data.First_name,
				['Business_hr']: res.data. Business,
			});
			console.log(res.data);
		});
	}, [updateFormData]);

	const handleChange = (e) => {
		updateFormData({
			...formData,
			// Trimming any whitespace
			[e.target.name]: e.target.value.trim(),
		});
	};

	const handleSubmit = (e) => {
		e.preventDefault();
		console.log(formData);

		axiosInstance.post(`hr/Complain/${complainID}/`, {

        Staff_issue:formData.Staff_issue,
		Date:formData.Date,
        Disciplinary_Action:formData.Disciplinary_Action,
        Business_hr:formData.Employee.Business,
        Employee :formData.Employee.First_name,


		})
        .then((res) => {
            history.push('/complain/');
            console.log(res);
            console.log(res.data);
        });


	};



    return (
      <div>
          <HrLayout>
        <Form>

          <FormItem label="Staff_issue">
            <Input 
            id="Staff_issue"
            name="Staff_issue" 
			label="Staff_issue"
			onChange={handleChange}
            placeholder="Input a staff issue here" 
            />
          </FormItem>

        <Form.Item label="Date">
        <DatePicker 
         label="Date"
         id="Date"
         name="Date" 
         onChange={handleChange}
        />
        </Form.Item>


          <FormItem label="Disciplinary_Action">
            <Input 
            id="Disciplinary_Action"
            name="Disciplinary_Action" 
			label="Disciplinary_Action"
			onChange={handleChange}
            placeholder="Input a business disciplinary action here" 
            />
          </FormItem>


          <FormItem label="Business">
            <Input 
            id="Business_hr"
            name="Business_hr" 
			label="Business_hr"
			onChange={handleChange}
            placeholder="Input a your business  here" 
            />
          </FormItem>

          <FormItem label="Employee ">
            <Input 
            	id="Employee "
              name="Employee " 
             label="Employee "
             onChange={handleChange}
            placeholder="Input a employee  here" 
            />
          </FormItem>



          <FormItem>
            <Button 
            type="submit"
						color="Blue"
						onClick={handleSubmit}
            >
            Create
            </Button>
          </FormItem>

        </Form>
        </HrLayout>
      </div>
    );

}
