import React from "react";
import axios from "axios";
import Applicants from "../../../components/job_application/applicant/applicant"
import axiosInstance from "../../../components/connection";
import { JobLayout } from "../../templates/joblayout";



class ApplicantList extends React.Component {
  state = {
    applicants: []
  };

  fetchApplicants = () => {
    axiosInstance
    .get("job/Applicant/").then(res => {
      this.setState({
        applicants: res.data
      });
    });
  }

  componentDidMount() {
    this.fetchApplicants();
  }

  componentWillReceiveProps(newProps) {
    if (newProps.token) {
      this.fetchApplicants();      
    }
  }

  render() {
    return (
      <div>
        <JobLayout>
        <Applicants data={this.state.applicants} /> <br />
        </JobLayout>
      </div>
    );
  }
}

export default ApplicantList;
